import React, { useState } from 'react';
import { CSSTransition } from 'react-transition-group';
import axios from 'axios';

const TeamRegistration = () => {
  const [teamName, setTeamName] = useState('');
  const [homeStadium, setHomeStadium] = useState('');
  const [players, setPlayers] = useState([
    { name: '', dob: '', type: 'Trong nước', notes: '' },
  ]);

  const handlePlayerChange = (index, event) => {
    const values = [...players];
    values[index][event.target.name] = event.target.value;
    setPlayers(values);
  };

  const handleAddPlayer = () => {
    if (players.length < 22) {
      setPlayers([...players, { name: '', dob: '', type: 'Trong nước', notes: '' }]);
    } else {
      alert('Số lượng cầu thủ đã đạt tối đa (22).');
    }
  };

  const handleRemovePlayer = (index) => {
    if (players.length > 1) {
      const values = [...players];
      values.splice(index, 1);
      setPlayers(values);
    } else {
        alert('Đội bóng phải có ít nhất 1 cầu thủ.');
    }
  };

  const validateForm = () => {
    if (players.length < 15 || players.length > 22) {
      alert(`Số lượng cầu thủ phải từ 15 đến 22. Hiện tại: ${players.length}`);
      return false;
    }

    let foreignPlayers = 0;
    for (const player of players) {
        if (!player.name || !player.dob) {
            alert('Tên và Ngày sinh của cầu thủ không được để trống.');
            return false;
        }

      const birthYear = new Date(player.dob).getFullYear();
      const currentYear = new Date().getFullYear();
      const age = currentYear - birthYear;
      if (age < 16 || age > 40) {
        alert(`Tuổi của cầu thủ "${player.name}" không hợp lệ. Tuổi phải từ 16 đến 40.`);
        return false;
      }

      if (player.type === 'Ngoài nước') {
        foreignPlayers++;
      }
    }

    if (foreignPlayers > 3) {
      alert(`Số lượng cầu thủ nước ngoài không được vượt quá 3. Hiện tại: ${foreignPlayers}`);
      return false;
    }

    if (!teamName.trim() || !homeStadium.trim()) {
        alert('Tên đội và Sân nhà không được để trống.');
        return false;
    }

    return true;
  };

  const [submissionStatus, setSubmissionStatus] = useState(null);
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (validateForm()) {
      try {
        const response = await fetch('http://localhost:3001/api/teams', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ teamName, homeStadium, players }),
        });

        const data = await response.json();

        if (!response.ok) {
          throw new Error(data.error || 'Something went wrong');
        }

        setSubmissionStatus('success');
        setMessage('Đăng ký đội bóng thành công!');
        setTeamName('');
        setHomeStadium('');
        setPlayers([{ name: '', dob: '', type: 'Trong nước', notes: '' }]);

      } catch (error) {
        setSubmissionStatus('error');
        setMessage(`Lỗi: ${error.message}`);
      } finally {
        setTimeout(() => {
          setSubmissionStatus(null);
          setMessage('');
        }, 3000);
      }
    }
  };

  return (
    <div>
      <h2 className="mb-4">Hồ Sơ Đội Bóng</h2>
      <CSSTransition
        in={submissionStatus !== null}
        timeout={500}
        classNames="alert-fade"
        unmountOnExit
      >
        <div className={`alert ${submissionStatus === 'success' ? 'alert-success' : 'alert-danger'}`}>
          {message}
        </div>
      </CSSTransition>
      <form onSubmit={handleSubmit}>
        <div className="row mb-3">
          <div className="col-md-6">
            <label htmlFor="teamName" className="form-label">Tên đội</label>
            <input
              type="text"
              id="teamName"
              className="form-control"
              value={teamName}
              onChange={(e) => setTeamName(e.target.value)}
              required
            />
          </div>
          <div className="col-md-6">
            <label htmlFor="homeStadium" className="form-label">Sân nhà</label>
            <input
              type="text"
              id="homeStadium"
              className="form-control"
              value={homeStadium}
              onChange={(e) => setHomeStadium(e.target.value)}
              required
            />
          </div>
        </div>

        <h3 className="mt-4 mb-3">Danh sách cầu thủ</h3>
        <div className="table-responsive">
          <table className="table table-bordered table-striped">
            <thead className="table-dark">
              <tr>
                <th style={{width: '5%'}}>STT</th>
                <th>Cầu Thủ</th>
                <th>Ngày Sinh</th>
                <th>Loại Cầu Thủ</th>
                <th>Ghi Chú</th>
                <th style={{width: '5%'}}></th>
              </tr>
            </thead>
            <tbody>
              {players.map((player, index) => (
                <tr key={index}>
                  <td className="align-middle text-center">{index + 1}</td>
                  <td>
                    <input
                      type="text"
                      name="name"
                      className="form-control"
                      value={player.name}
                      onChange={(e) => handlePlayerChange(index, e)}
                      required
                    />
                  </td>
                  <td>
                    <input
                      type="date"
                      name="dob"
                      className="form-control"
                      value={player.dob}
                      onChange={(e) => handlePlayerChange(index, e)}
                      required
                    />
                  </td>
                  <td>
                    <select
                      name="type"
                      className="form-select"
                      value={player.type}
                      onChange={(e) => handlePlayerChange(index, e)}
                    >
                      <option value="Trong nước">Trong nước</option>
                      <option value="Ngoài nước">Ngoài nước</option>
                    </select>
                  </td>
                  <td>
                    <input
                      type="text"
                      name="notes"
                      className="form-control"
                      value={player.notes}
                      onChange={(e) => handlePlayerChange(index, e)}
                    />
                  </td>
                  <td className="align-middle text-center">
                    <button type="button" className="btn btn-danger btn-sm" onClick={() => handleRemovePlayer(index)}>Xóa</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        <button type="button" className="btn btn-primary mt-2" onClick={handleAddPlayer}>
          Thêm cầu thủ
        </button>
        <hr className="my-4" />
        <button type="submit" className="btn btn-success btn-lg">
          Đăng ký đội bóng
        </button>
      </form>
    </div>
  );
};

export default TeamRegistration;
