import React, { useState } from 'react';

const TeamRegistration = () => {
  const [teamName, setTeamName] = useState('');
  const [homeStadium, setHomeStadium] = useState('');
  const [players, setPlayers] = useState([
    { name: '', dob: '', type: 'Trong nước', notes: '' },
  ]);

  const handlePlayerChange = (index, event) => {
    const values = [...players];
    values[index][event.target.name] = event.target.value;
    setPlayers(values);
  };

  const handleAddPlayer = () => {
    if (players.length < 22) {
      setPlayers([...players, { name: '', dob: '', type: 'Trong nước', notes: '' }]);
    } else {
      alert('Số lượng cầu thủ đã đạt tối đa (22).');
    }
  };

  const handleRemovePlayer = (index) => {
    if (players.length > 1) {
      const values = [...players];
      values.splice(index, 1);
      setPlayers(values);
    } else {
        alert('Đội bóng phải có ít nhất 1 cầu thủ.');
    }
  };

  const validateForm = () => {
    // Rule: Team size 15-22
    if (players.length < 15 || players.length > 22) {
      alert(`Số lượng cầu thủ phải từ 15 đến 22. Hiện tại: ${players.length}`);
      return false;
    }

    let foreignPlayers = 0;
    for (const player of players) {
      // Rule: Player age 16-40
      const birthYear = new Date(player.dob).getFullYear();
      const currentYear = new Date().getFullYear();
      const age = currentYear - birthYear;
      if (age < 16 || age > 40) {
        alert(`Tuổi của cầu thủ "${player.name}" không hợp lệ. Tuổi phải từ 16 đến 40.`);
        return false;
      }

      // Rule: Max 3 foreign players
      if (player.type === 'Ngoài nước') {
        foreignPlayers++;
      }
    }

    if (foreignPlayers > 3) {
      alert(`Số lượng cầu thủ nước ngoài không được vượt quá 3. Hiện tại: ${foreignPlayers}`);
      return false;
    }

    if (!teamName.trim() || !homeStadium.trim()) {
        alert('Tên đội và Sân nhà không được để trống.');
        return false;
    }

    return true;
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (validateForm()) {
      alert('Hồ sơ đội bóng hợp lệ!\n' + JSON.stringify({ teamName, homeStadium, players }, null, 2));
      // Here you would typically send the data to the server
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: 'auto' }}>
      <h2>Hồ Sơ Đội Bóng</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '15px' }}>
          <label>Tên đội:</label>
          <input
            type="text"
            value={teamName}
            onChange={(e) => setTeamName(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>
        <div style={{ marginBottom: '15px' }}>
          <label>Sân nhà:</label>
          <input
            type="text"
            value={homeStadium}
            onChange={(e) => setHomeStadium(e.target.value)}
            required
            style={{ width: '100%', padding: '8px' }}
          />
        </div>

        <h3>Danh sách cầu thủ</h3>
        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>STT</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Cầu Thủ</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Ngày Sinh</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Loại Cầu Thủ</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}>Ghi Chú</th>
              <th style={{ border: '1px solid #ddd', padding: '8px' }}></th>
            </tr>
          </thead>
          <tbody>
            {players.map((player, index) => (
              <tr key={index}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{index + 1}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <input
                    type="text"
                    name="name"
                    value={player.name}
                    onChange={(e) => handlePlayerChange(index, e)}
                    required
                    style={{ width: '95%' }}
                  />
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <input
                    type="date"
                    name="dob"
                    value={player.dob}
                    onChange={(e) => handlePlayerChange(index, e)}
                    required
                    style={{ width: '95%' }}
                  />
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <select
                    name="type"
                    value={player.type}
                    onChange={(e) => handlePlayerChange(index, e)}
                     style={{ width: '95%' }}
                  >
                    <option value="Trong nước">Trong nước</option>
                    <option value="Ngoài nước">Ngoài nước</option>
                  </select>
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>
                  <input
                    type="text"
                    name="notes"
                    value={player.notes}
                    onChange={(e) => handlePlayerChange(index, e)}
                     style={{ width: '95%' }}
                  />
                </td>
                <td style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'center' }}>
                  <button type="button" onClick={() => handleRemovePlayer(index)}>Xóa</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <button type="button" onClick={handleAddPlayer} style={{ marginTop: '10px' }}>
          Thêm cầu thủ
        </button>
        <hr style={{ margin: '20px 0' }} />
        <button type="submit" style={{ padding: '10px 20px', fontSize: '16px' }}>
          Đăng ký
        </button>
      </form>
    </div>
  );
};

export default TeamRegistration;
